import React, { useState } from "react";
import { TextField } from "@mui/material";

function CombatantFormInput() {
  const [combatants, setAllcombatants] = useState([
    { name: "a", hp: "", init: null, bonus: null },
    { name: "b", hp: "", init: null, bonus: null },
  ]);
  const test = (e) => {
    e.preventDefault();
  };

  return (
    <form onSubmit={test}>
      {combatants.map((combatant, index) => (
        <div>
          {/* ðŸ‘‡ï¸ iterate object KEYS */}
          {Object.keys(combatant).map((key, index) => {
            return (
              <div key={index}>
                <h2>
                  {key}: {combatant[key]}
                </h2>
              </div>
            );
          })}

          <br />
          <br />
          <br />
        </div>
      ))}
      <div>
        <input type="text" id="name"></input>
        <input type="text" id="hp"></input>
        <input type="Submit"></input>
      </div>
    </form>
  );
}
export default CombatantFormInput;
