import { Container } from "@mui/system";
import React, { useState } from "react";

function CombatantForm() {
  const [combatants, setAllcombatants] = useState([
    { name: "", hp: "", init: 0, bonus: 0, type: "player" },
  ]);

  return (
    <div>
      <form>
        {combatants.map((combatant, index) => (
          <Container key={index}>
            <input
              type="text"
              name="name"
              placeholder="name"
              value={combatants.name}
            ></input>
            <input
              type="text"
              name="hp"
              placeholder="hp"
              value={combatants.hp}
            ></input>
            <input
              type="text"
              name="init"
              placeholder="init"
              value={combatants.init}
            ></input>
            <input
              type="text"
              name="bonus"
              placeholder="bonus"
              value={combatants.bonus}
            ></input>
          </Container>
        ))}
      </form>
    </div>
  );
}

export default CombatantForm;
