import React, { useState } from "react";
import { TextField } from "@mui/material";

function CombatantFormInput() {
  const [combatants, setAllcombatants] = useState([
    { name: "a", hp: "", init: null, bonus: null },
    { name: "b", hp: "", init: null, bonus: null },
  ]);
  const test = (e) => {
    e.preventDefault();
  };

  return (
    <form onSubmit={test}>
      <div>
        {/* 👇️ iterate object KEYS */}
        {Object.keys(combatants).map((key, index) => {
          return (
            <div key={index}>
              <h2>
                {key}: {combatants[key]}
              </h2>

              <hr />
            </div>
          );
        })}

        <br />
        <br />
        <br />

        {/* 👇️ iterate object VALUES */}
        {Object.values(combatants).map((value, index) => {
          return (
            <div key={index}>
              <h2>{value}</h2>

              <hr />
            </div>
          );
        })}
      </div>
      <div>
        <input type="text" id="name"></input>
        <input type="text" id="hp"></input>
        <input type="Submit"></input>
      </div>
    </form>
  );
}
export default CombatantFormInput;
