function CombatantFormInput() {
  const [allPlayers, setAllPlayers] = useState([
    { name: "", description: "", price: null, rating: null },
  ]);
  const handleSubmit = (e) => {
    e.preventDefault();
    const { name, hp } = e.target.elements;
    console.log({ name: name.value, hp: hp.value });
  };
  return (
    <form onSubmit={handleSubmit}>
      <div>
        <input type="text" id="name"></input>
        <input type="text" id="hp"></input>
        <input type="Submit"></input>
      </div>
    </form>
  );
}
export default CombatantFormInput;
