import React, { useState } from "react";
import { TextField } from "@mui/material";
import { Button } from "@mui/material";
import { CoPresent } from "@mui/icons-material";
import { Box } from "@mui/system";

function CombatantFormInput(type) {
  const [combatants, setAllcombatants] = useState([
    { name: "", hp: "", init: 0, bonus: 0, type: "player" },
  ]);

  const handleInputChange = (index, event) => {
    const values = [...combatants];
    console.log(values);
    values[index][event.target.name] = event.target.value;
    setAllcombatants(values);
    console.log(index, event.target.name, event.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(combatants);
  };

  const handleNewField = (type) => {
    setAllcombatants([
      ...combatants,
      {
        name: "",
        hp: "",
        init: 0,
        bonus: 0,
        type: type,
      },
    ]);
  };

  const handleRemoveField = (index) => {
    const values = [...combatants];
    values.splice(index, 1);
    setAllcombatants(values);
  };

  return (
    <div>
      <h1>Players</h1>
      <form onSubmit={handleSubmit}>
        {combatants.map((combatant, index) => (
          <Box
            component="form"
            sx={{
              "& .MuiTextField-root": { m: 1, width: "25ch" },
            }}
            noValidate
            autoComplete="off"
            key={index}
          >
            <TextField
              name="name"
              label="name"
              value={combatant.name}
              onChange={(event) => handleInputChange(index, event)}
            ></TextField>
            <TextField
              name="hp"
              label="hp"
              value={combatant.hp}
              style={{ width: "70px" }}
              onChange={(event) => handleInputChange(index, event)}
            ></TextField>
            <TextField
              name="init"
              label="init"
              value={combatant.init}
              style={{ width: "70px" }}
              onChange={(event) => handleInputChange(index, event)}
            ></TextField>
            <TextField
              name="bonus"
              label="bonus"
              value={combatant.bonus}
              style={{ width: "70px" }}
              onChange={(event) => handleInputChange(index, event)}
            ></TextField>
            <Button onClick={() => handleRemoveField(index)}>X</Button>
          </Box>
        ))}
        <Button
          variant="contained"
          color="primary"
          type="submit"
          onClick={handleSubmit}
        >
          Submit
        </Button>
        <Button onClick={handleNewField}>Add new</Button>
      </form>
    </div>
  );
}
export default CombatantFormInput;
