import React, { useState } from "react";
import { TextField } from "@mui/material";
import { Button } from "@mui/material";
import { CoPresent } from "@mui/icons-material";

function CombatantFormInput() {
  const [combatants, setAllcombatants] = useState([
    { id: 1, name: "", hp: "", init: 0, bonus: 0, type: "player" },
    { id: 2, name: "", hp: "", init: 0, bonus: 0, type: "player" },
  ]);
  const test = (e) => {
    e.preventDefault();
  };

  const handleInputChange = (index, event) => {
    console.log(index, event.target.name, event.target.value);
  };

  return (
    <div>
      <form>
        {combatants.map((combatant, index) => (
          <div key={combatant.id}>
            {Object.keys(combatant).map((key) => {
              if (key !== "type" && key !== "id") {
                return (
                  <TextField
                    name={key}
                    label={key}
                    value={combatant[key]}
                    onChange={(event) => handleInputChange(index, event)}
                  ></TextField>
                );
              }
              return null;
            })}
          </div>
        ))}
        {/* <Button variant="contained" color="primary" type="submit">
          Submit
        </Button> */}
      </form>
    </div>
  );
}
export default CombatantFormInput;
