import React, { useState } from "react";

function CombatantFormInput() {
  const [combatants, setAllcombatants] = useState([
    { name: "", hp: "", init: null, bonus: null },
    { name: "", hp: "", init: null, bonus: null },
  ]);
  const test = (e) => {
    e.preventDefault();
    console.log([
      combatants.forEach((item) => {
        console.log(item);
      }),
    ]);
  };
  return (
    <form onSubmit={test}>
      {combatants.map((combatant, index) => (
        <div key={index}></div>
      ))}
      <div>
        <input type="text" id="name"></input>
        <input type="text" id="hp"></input>
        <input type="Submit"></input>
      </div>
    </form>
  );
}
export default CombatantFormInput;
